mapper.select:
select
id, major_id, subject_id, teacher_id, classroom_id, time
from student_task.class_task


        and id = #{id}


        and major_id = #{majorId}


        and subject_id = #{subjectId}


        and teacher_id = #{teacherId}


        and classroom_id = #{classroomId}


        and time = #{time}
